import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { validateFile, formatFileSize } from '../services/api';
import './FileUpload.css';

const FileUpload = ({ onFilesSelected, processing, selectedLanguage }) => {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);

  const onDrop = useCallback((acceptedFiles, rejectedFiles) => {
    setDragActive(false);

    // Hatalƒ± dosyalarƒ± kontrol et
    if (rejectedFiles.length > 0) {
      console.warn('Reddedilen dosyalar:', rejectedFiles);
    }

    // Dosyalarƒ± validate et
    const validFiles = [];
    const errors = [];

    acceptedFiles.forEach(file => {
      try {
        validateFile(file);
        validFiles.push(file);
      } catch (error) {
        errors.push(`${file.name}: ${error.message}`);
      }
    });

    if (errors.length > 0) {
      alert('Dosya hatalarƒ±:\n' + errors.join('\n'));
    }

    if (validFiles.length > 0) {
      setSelectedFiles(validFiles);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    onDragEnter: () => setDragActive(true),
    onDragLeave: () => setDragActive(false),
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.bmp', '.tiff'],
      'application/pdf': ['.pdf']
    },
    maxFiles: 10,
    maxSize: 50 * 1024 * 1024, // 50MB
    disabled: processing
  });

  const handleProcessFiles = () => {
    if (selectedFiles.length > 0) {
      onFilesSelected(selectedFiles);
      setSelectedFiles([]);
    }
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const clearFiles = () => {
    setSelectedFiles([]);
  };

  return (
    <div className="file-upload card">
      <h3>üìÇ Dosya Y√ºkleme</h3>

      <div 
        {...getRootProps()} 
        className={`dropzone ${isDragActive || dragActive ? 'active' : ''} ${processing ? 'disabled' : ''}`}
      >
        <input {...getInputProps()} />

        <div className="dropzone-content">
          <div className="upload-icon">
            {processing ? '‚è≥' : isDragActive ? 'üì•' : 'üìÅ'}
          </div>

          <div className="upload-text">
            {processing ? (
              <>
                <h4>ƒ∞≈üleniyor...</h4>
                <p>L√ºtfen bekleyin</p>
              </>
            ) : isDragActive ? (
              <>
                <h4>Dosyalarƒ± buraya bƒ±rakƒ±n</h4>
                <p>Dosyalar y√ºklenecek</p>
              </>
            ) : (
              <>
                <h4>Dosya y√ºklemek i√ßin tƒ±klayƒ±n veya s√ºr√ºkleyin</h4>
                <p>G√∂r√ºnt√º (JPG, PNG, GIF, BMP, TIFF) ve PDF dosyalarƒ±nƒ± destekliyoruz</p>
              </>
            )}
          </div>

          <div className="upload-limits">
            <span>Maksimum 10 dosya ‚Ä¢ En fazla 50MB/dosya</span>
          </div>
        </div>
      </div>

      {selectedFiles.length > 0 && (
        <div className="selected-files">
          <div className="files-header">
            <h4>Se√ßili Dosyalar ({selectedFiles.length})</h4>
            <button 
              onClick={clearFiles}
              className="btn btn-secondary btn-sm"
              disabled={processing}
            >
              Temizle
            </button>
          </div>

          <div className="files-list">
            {selectedFiles.map((file, index) => (
              <div key={index} className="file-item">
                <div className="file-info">
                  <div className="file-icon">
                    {file.type.startsWith('image/') ? 'üñºÔ∏è' : 'üìÑ'}
                  </div>
                  <div className="file-details">
                    <span className="file-name">{file.name}</span>
                    <span className="file-size">{formatFileSize(file.size)}</span>
                  </div>
                </div>

                <button
                  onClick={() => removeFile(index)}
                  className="remove-file"
                  disabled={processing}
                  title="Dosyayƒ± kaldƒ±r"
                >
                  ‚úï
                </button>
              </div>
            ))}
          </div>

          <div className="process-section">
            <div className="language-info">
              <span className="language-label">Se√ßili Dil:</span>
              <span className="language-value">{selectedLanguage}</span>
            </div>

            <button
              onClick={handleProcessFiles}
              className="btn btn-primary"
              disabled={processing || selectedFiles.length === 0}
            >
              {processing ? (
                <>
                  <div className="loading"></div>
                  ƒ∞≈üleniyor...
                </>
              ) : (
                <>
                  üöÄ OCR ƒ∞≈ülemini Ba≈ülat
                </>
              )}
            </button>
          </div>
        </div>
      )}

      <div className="upload-info">
        <div className="info-grid">
          <div className="info-item">
            <span className="info-icon">‚úÖ</span>
            <span>Y√ºksek Doƒüruluk OCR</span>
          </div>
          <div className="info-item">
            <span className="info-icon">üåç</span>
            <span>√áoklu Dil Desteƒüi</span>
          </div>
          <div className="info-item">
            <span className="info-icon">üîí</span>
            <span>G√ºvenli ƒ∞≈üleme</span>
          </div>
          <div className="info-item">
            <span className="info-icon">‚ö°</span>
            <span>Hƒ±zlƒ± Sonu√ßlar</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
